import React from "react"
import { useState } from "react";

function TodoItems(props){
const [inputMouse,setInput]=useState(false)
function handleChange(){
    setInput(true);
}

    return <li onClick={handleChange} onClick style={{textDecoration:inputMouse?"line-through":"none"}}>{props.text}</li>
    
}


export default TodoItems;
