import React from "react";
import { useState } from "react";

function App() {
 const [name,SetHeading]=useState(" ")
 const[Heading,SetName]=useState(" ")

  function handleInput(event){
    SetHeading(event.target.value);
  }

  function SetHeadValue(event){
    SetName(name)
    event.preventDefault();
  }
  return (
    <div className="container">
      <h1>{Heading}</h1>
      <input onChange={handleInput} type="text" placeholder="What's your name?" />
  <p>{new Date().toLocaleTimeString()}</p>
      <button onClick={SetHeadValue} >Submit</button>
    </div>
  );
}

export default App;
