import React from "react";
import { useState } from "react";

function App() {
  const[isMouseOver, setMouseOver]=useState(false);
  const [name,setName]=useState(" ");
  const [Heading,setHeading]=useState(" ");

  function handleMouseOver(){
setMouseOver(true);
  }
  function handleMouseLeave(){
    setMouseOver(false)
  }

  function headingName(event){
    setName(event.target.value);
  }
  function handleClick(event){
    setHeading(name);
    event.preventDefault();
  }

  return (
    <div className="container">
      <p>{new Date().toLocaleTimeString()}</p>
      <h1 onChange={headingName}>{Heading}</h1>
      <input type="text" placeholder="What's your name?" value={name} />
      <button onClick={handleClick} style={{background:isMouseOver?"black":"white"}} onMouseOut={handleMouseLeave} onMouseOver={handleMouseOver}>Submit</button>
    </div>
  );
}

export default App;
