import React from "react";
import { useState } from "react";

function App() {
  const [heading,setHeading]=useState("Hello");
  const [isMoveOver,SetMouseMove]=useState(false)
    function handleName(){
    setHeading("Form Submitted");
  }
  function MouseMove(){
SetMouseMove(true)
  }
  return (
    <div className="container">
      <h1>{heading}</h1>
      <input type="text" placeholder="What's your name?" />
  <p>{new Date().toLocaleTimeString()}</p>
      <button style={{background:isMoveOver?"black":"whte"}} onClick={handleName}onMouseMove={MouseMove} >Submit</button>
    </div>
  );
}

export default App;
