import React,{useState} from "react"
function App(){

  const [count,setsate]=useState(0);
  function increase(){

    setsate(count+1)
  }
decrease=>(setsate(count-1))
  return (
    <div>
      <h1>{count}</h1>
      <button onClick={increase}>+</button>
      <button onClick={decrease}>-</button>
    </div>
  )
}
export default App;
