import React, {Component }from 'react'

class RootComp  extends Component {
    constructor(){
        super()
    this.state = {isConnected:false  }}
    render() { 
        let connectionStat;
        if(this.state.isConnected){
            connectionStat="You are connected to conservices"
        }
        else{
            connectionStat="You are disconnected from our services"
        }
        return (
        <div>
            <p>{connectionStat}</p>
        </div>  
        );
    }
}
 
export default RootComp ;
