import React,{useState} from "react";




function App() {
  const [fullName,setFName]=useState({
    fName:" ",
    lName:" "
  })

  function handleFirst(event){
  const newValue=event.target.value;
  const currentValue=event.target.name;

setFName(prevValue=>{
if(currentValue==="fName"){
  return{
    fName:newValue,
    lName:prevValue.lName
  };
}
else if(currentValue==="lastName"){
  return{
    fName:prevValue.fName,
    lName:newValue
  };
}
})

  }

  return (
    <div className="container">
      <h1>Hello {fullName.fName} {fullName.lName}</h1>
      <form>
        <input onChange={handleFirst} name="fName" placeholder="First Name" value={fullName.fName} />
        <input onChange={handleFirst} name="lName" placeholder="Last Name" value={fullName.lName} />
        <button>Submit</button>
      </form>
    </div>
  );
}

export default App;
